Spree.Views.Images.UploadProgress = Backbone.View.extend({
  tagName: "div",

  // Cache the template function for a single item.
  template: HandlebarsTemplates["products/upload_progress"],

  initialize: function() {
    this.listenTo(this.model, 'change:progress', this.updateProgressBar);
    this.listenTo(this.model, 'change', this.render);
    this.listenTo(this.model, 'destroy', this.remove);
  },

  events: {
    "clear" : "clear"
  },

  className: 'col-sm-6 col-md-4 mb-3',

  attributes: function() {
    return {
      "data-upload-id": this.model.cid
    }
  },

  render: function() {
    // Skip progress bar update for better performance
    var changedAttrs = Object.keys(this.model.changed);
    if(changedAttrs.length === 1 && changedAttrs[0] == 'progress') return this;

    this.el.innerHTML = this.template(this.model.toJSON());
    this.updateProgressBar();
    return this;
  },

  updateProgressBar: function() {
    var progressBar = this.el.querySelector('.progress-bar');
    var percent = this.model.get('progress');
    progressBar.setAttribute('aria-valuenow', percent);
    progressBar.style.width = percent + '%';
    progressBar.innerHTML = percent + '%';
    return this;
  },

  // Remove the item, destroy the model
  clear: function() {
    this.model.destroy();
  }
});
Spree.Views.Order.Address = Backbone.View.extend({
  initialize: function(options) {
    // read initial values from page
    this.onChange();

    this.render();
    this.listenTo(this.model, "change", this.render);

    this.stateSelect =
      new Spree.Views.StateSelect({
        model: this.model,
        el: this.$el
      });
  },

  events: {
    "change": "onChange",
  },

  onChange: function() {
    this.model.set(this.getValues())
  },

  eachField: function(callback){
    var view = this;
    var fields = ["firstname", "lastname", "company", "address1", "address2",
      "city", "zipcode", "phone", "country_id"];
    _.each(fields, function(field) {
      var el = view.$('[name$="[' + field + ']"]');
      if (el.length) callback(field, el);
    });
  },

  getValues: function() {
    var attributes = {};
    this.eachField(function(name, el) {
      attributes[name] = el.val();
    });
    return attributes;
  },

  render: function() {
    var model = this.model;
    this.eachField(function(name, el) {
      el.val(model.get(name))
    })
  }
});
Spree.Views.Order.DetailsAdjustments = Backbone.View.extend({
  initialize: function() {
    this.listenTo(this.model, "change", this.render);
    this.render()
  },

  adjustmentTotals: function() {
    var totals = {};
    var collection = this.collection ? this.collection.chain() : _.chain([this.model]);
    collection
      .map(function(item){ return item.get("adjustments") || [] })
      .flatten(true)
      .each(function(adjustment){
        var label = adjustment.label;

        /* Fixme: because this is done in JS, we only have floating point math */
        totals[label] = (totals[label] || 0);
        totals[label] += Number(adjustment.amount);
      });
    return totals;
  },

  render: function() {
    var model = this.model;
    var tbody = this.$('tbody');
    var adjustmentTotals = this.adjustmentTotals()

    tbody.empty();
    _.each(adjustmentTotals, function(amount, label) {
       var html = HandlebarsTemplates["orders/details_adjustment_row"]({
         label: label,
         amount: Spree.formatMoney(amount, model.get("currency"))
       });
       tbody.append(html);
    });

    this.$el.toggleClass("hidden", _.isEmpty(adjustmentTotals));
  }
})
;
